import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { 
  Star, 
  MapPin, 
  Calendar, 
  ShoppingCart, 
  User, 
  MessageCircle,
  ArrowLeft,
  Package,
  Clock
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [bookingQuantity, setBookingQuantity] = useState(1);
  const [booking, setBooking] = useState(false);
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [deliveryAddress, setDeliveryAddress] = useState({
    street: '',
    city: '',
    state: '',
    district: '',
    zipCode: ''
  });
  const [activeImage, setActiveImage] = useState(0);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/products/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Error fetching product:', error);
      toast.error('Error loading product details');
    } finally {
      setLoading(false);
    }
  };

  const handleBooking = async () => {
    if (!user) {
      toast.error('Please login to book products');
      navigate('/login');
      return;
    }

    if (bookingQuantity > product.quantity) {
      toast.error(`Only ${product.quantity} ${product.unit} available`);
      return;
    }

    // Validate delivery address
    if (!deliveryAddress.street || !deliveryAddress.city || !deliveryAddress.state || !deliveryAddress.district) {
      toast.error('Please fill in all required address fields');
      return;
    }

    try {
      setBooking(true);
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `/api/bookings`,
        { 
          productId: id,
          quantity: bookingQuantity,
          deliveryAddress,
          notes: ''
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const { deliveryInfo } = response.data;
      const deliveryMessage = deliveryInfo.isSameDistrict 
        ? 'Free delivery available in your district!'
        : `Delivery fee: ₹${deliveryInfo.deliveryFee}`;
      
      toast.success(`Booking successful! ${deliveryMessage} The seller has been notified.`);
      fetchProduct(); // Refresh product data
      setBookingQuantity(1);
      setShowBookingForm(false);
      setDeliveryAddress({
        street: '',
        city: '',
        state: '',
        district: '',
        zipCode: ''
      });
    } catch (error) {
      console.error('Booking error:', error);
      toast.error(error.response?.data?.message || 'Error processing booking');
    } finally {
      setBooking(false);
    }
  };

  const formatCurrency = (amount) => {
    return `₹${parseFloat(amount).toLocaleString('en-IN')}`;
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-IN');
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="spinner"></div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Product Not Found</h2>
          <p className="text-gray-600 mb-4">The product you're looking for doesn't exist.</p>
          <button
            onClick={() => navigate('/products')}
            className="btn-primary"
          >
            Back to Products
          </button>
        </div>
      </div>
    );
  }

  const isOutOfStock = product.quantity === 0;
  const isOwner = user && product.seller._id === user._id;

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Back Button */}
        <button
          onClick={() => navigate('/products')}
          className="flex items-center space-x-2 text-gray-600 hover:text-gray-900 mb-6"
        >
          <ArrowLeft size={20} />
          <span>Back to Products</span>
        </button>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Product Images */}
          <div className="space-y-4">
            <div className="aspect-w-1 aspect-h-1 bg-gray-200 rounded-lg overflow-hidden">
              <img
                src={product.images[activeImage] || '/placeholder-product.jpg'}
                alt={product.name}
                className="w-full h-96 object-cover"
              />
            </div>
            {product.images.length > 1 && (
              <div className="grid grid-cols-4 gap-2">
                {product.images.map((image, index) => (
                  <button
                    key={index}
                    onClick={() => setActiveImage(index)}
                    className={`aspect-w-1 aspect-h-1 rounded-lg overflow-hidden border-2 ${
                      activeImage === index ? 'border-green-500' : 'border-gray-200'
                    }`}
                  >
                    <img
                      src={image}
                      alt={`${product.name} ${index + 1}`}
                      className="w-full h-20 object-cover"
                    />
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Product Info */}
          <div className="space-y-6">
            {/* Header */}
            <div>
              <div className="flex items-center justify-between mb-2">
                <h1 className="text-3xl font-bold text-gray-900">{product.name}</h1>
                <div className="text-3xl font-bold text-green-600">
                  {formatCurrency(product.price)}
                </div>
              </div>
              
              <div className="flex items-center space-x-4 mb-4">
                <div className="flex items-center space-x-1">
                  <Star size={16} className="text-yellow-400 fill-current" />
                  <span className="text-sm text-gray-600">
                    {product.rating.toFixed(1)} ({product.reviews.length} reviews)
                  </span>
                </div>
                {product.isOrganic && (
                  <span className="badge badge-success">Organic</span>
                )}
              </div>

              <p className="text-gray-600 leading-relaxed">{product.description}</p>
            </div>

            {/* Stock Status */}
            <div className="bg-white rounded-lg p-4 border">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <Package size={20} className="text-gray-600" />
                  <span className="font-medium text-gray-900">Availability</span>
                </div>
                {isOutOfStock ? (
                  <span className="text-red-600 font-medium">Out of Stock</span>
                ) : (
                  <span className="text-green-600 font-medium">
                    {product.quantity} {product.unit} available
                  </span>
                )}
              </div>
            </div>

            {/* Purchase Section */}
            {!isOwner && (
              <div className="bg-white rounded-lg p-6 border">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Book & Get Delivered</h3>
                
                {isOutOfStock ? (
                  <div className="text-center py-4">
                    <p className="text-gray-600 mb-4">This product is currently out of stock.</p>
                    <button
                      onClick={() => navigate('/products')}
                      className="btn-secondary"
                    >
                      Browse Other Products
                    </button>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {!showBookingForm ? (
                      <>
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2">
                            Quantity ({product.unit})
                          </label>
                          <input
                            type="number"
                            min="1"
                            max={product.quantity}
                            value={bookingQuantity}
                            onChange={(e) => setBookingQuantity(parseInt(e.target.value) || 1)}
                            className="form-input w-full"
                          />
                        </div>
                        
                        <div className="flex items-center justify-between py-2 border-t">
                          <span className="font-medium">Total:</span>
                          <span className="text-xl font-bold text-green-600">
                            {formatCurrency(product.price * bookingQuantity)}
                          </span>
                        </div>

                        <button
                          onClick={() => setShowBookingForm(true)}
                          disabled={!user}
                          className="btn-primary w-full flex items-center justify-center space-x-2"
                        >
                          <Package size={20} />
                          <span>Book & Get Delivered</span>
                        </button>

                        {!user && (
                          <p className="text-sm text-gray-600 text-center">
                            Please <button onClick={() => navigate('/login')} className="text-green-600 hover:underline">login</button> to book
                          </p>
                        )}
                      </>
                    ) : (
                      <div className="space-y-4">
                        <h4 className="font-medium text-gray-900">Delivery Address</h4>
                        
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2">
                            Street Address *
                          </label>
                          <input
                            type="text"
                            value={deliveryAddress.street}
                            onChange={(e) => setDeliveryAddress(prev => ({ ...prev, street: e.target.value }))}
                            className="form-input w-full"
                            placeholder="Enter street address"
                          />
                        </div>

                        <div className="grid grid-cols-2 gap-3">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              City *
                            </label>
                            <input
                              type="text"
                              value={deliveryAddress.city}
                              onChange={(e) => setDeliveryAddress(prev => ({ ...prev, city: e.target.value }))}
                              className="form-input w-full"
                              placeholder="Enter city"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              State *
                            </label>
                            <input
                              type="text"
                              value={deliveryAddress.state}
                              onChange={(e) => setDeliveryAddress(prev => ({ ...prev, state: e.target.value }))}
                              className="form-input w-full"
                              placeholder="Enter state"
                            />
                          </div>
                        </div>

                        <div className="grid grid-cols-2 gap-3">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              District *
                            </label>
                            <input
                              type="text"
                              value={deliveryAddress.district}
                              onChange={(e) => setDeliveryAddress(prev => ({ ...prev, district: e.target.value }))}
                              className="form-input w-full"
                              placeholder="Enter district"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              ZIP Code
                            </label>
                            <input
                              type="text"
                              value={deliveryAddress.zipCode}
                              onChange={(e) => setDeliveryAddress(prev => ({ ...prev, zipCode: e.target.value }))}
                              className="form-input w-full"
                              placeholder="Enter ZIP code"
                            />
                          </div>
                        </div>

                        <div className="flex items-center justify-between py-2 border-t">
                          <span className="font-medium">Total:</span>
                          <span className="text-xl font-bold text-green-600">
                            {formatCurrency(product.price * bookingQuantity)}
                          </span>
                        </div>

                        <div className="flex space-x-3">
                          <button
                            onClick={() => setShowBookingForm(false)}
                            className="btn-secondary flex-1"
                          >
                            Back
                          </button>
                          <button
                            onClick={handleBooking}
                            disabled={booking || !user}
                            className="btn-primary flex-1 flex items-center justify-center space-x-2"
                          >
                            <Package size={20} />
                            <span>{booking ? 'Processing...' : 'Confirm Booking'}</span>
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </div>
            )}

            {/* Seller Info */}
            <div className="bg-white rounded-lg p-4 border">
              <div className="flex items-center space-x-3 mb-3">
                <User size={20} className="text-gray-600" />
                <span className="font-medium text-gray-900">Seller Information</span>
              </div>
              <div className="space-y-2">
                <p className="text-gray-900 font-medium">{product.seller.name}</p>
                {product.seller.farmName && (
                  <p className="text-gray-600">{product.seller.farmName}</p>
                )}
                <div className="flex items-center space-x-1 text-gray-600">
                  <Star size={14} className="text-yellow-400 fill-current" />
                  <span>{product.seller.rating?.toFixed(1) || 'No rating'} ({product.seller.totalSales || 0} sales)</span>
                </div>
                {product.location && (
                  <div className="flex items-center space-x-1 text-gray-600">
                    <MapPin size={14} />
                    <span>{product.location.city}, {product.location.state}</span>
                  </div>
                )}
              </div>
            </div>

            {/* Product Details */}
            <div className="bg-white rounded-lg p-4 border">
              <h3 className="font-medium text-gray-900 mb-3">Product Details</h3>
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-600">Category:</span>
                  <span className="text-gray-900">{product.category}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Unit:</span>
                  <span className="text-gray-900">{product.unit}</span>
                </div>
                {product.harvestDate && (
                  <div className="flex justify-between">
                    <span className="text-gray-600">Harvest Date:</span>
                    <span className="text-gray-900">{formatDate(product.harvestDate)}</span>
                  </div>
                )}
                {product.expiryDate && (
                  <div className="flex justify-between">
                    <span className="text-gray-600">Expiry Date:</span>
                    <span className="text-gray-900">{formatDate(product.expiryDate)}</span>
                  </div>
                )}
                <div className="flex justify-between">
                  <span className="text-gray-600">Views:</span>
                  <span className="text-gray-900">{product.views}</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Reviews Section */}
        {product.reviews.length > 0 && (
          <div className="mt-12">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Reviews</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {product.reviews.map((review, index) => (
                <div key={index} className="bg-white rounded-lg p-4 border">
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-medium text-gray-900">{review.user.name}</span>
                    <div className="flex items-center space-x-1">
                      <Star size={14} className="text-yellow-400 fill-current" />
                      <span className="text-sm text-gray-600">{review.rating}</span>
                    </div>
                  </div>
                  <p className="text-gray-600 text-sm mb-2">{review.comment}</p>
                  <div className="flex items-center space-x-1 text-xs text-gray-500">
                    <Clock size={12} />
                    <span>{formatDate(review.createdAt)}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductDetail;
