import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { Upload, X, Plus, Gavel } from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

const CreateAuction = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [images, setImages] = useState([]);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    livestockType: 'Cow',
    breed: '',
    age: '',
    weight: '',
    healthStatus: 'Good',
    startingBid: '',
    minBidIncrement: '100',
    startDate: '',
    endDate: '',
    location: {
      city: '',
      state: ''
    }
  });

  const livestockTypes = [
    { value: 'Cow', label: 'Cow' },
    { value: 'Horse', label: 'Horse' },
    { value: 'Buffalo', label: 'Buffalo' },
    { value: 'Sheep', label: 'Sheep' },
    { value: 'Goat', label: 'Goat' },
    { value: 'Pig', label: 'Pig' },
    { value: 'Chicken', label: 'Chicken' },
    { value: 'Duck', label: 'Duck' },
    { value: 'Other', label: 'Other' }
  ];

  const healthStatuses = [
    { value: 'Excellent', label: 'Excellent' },
    { value: 'Good', label: 'Good' },
    { value: 'Fair', label: 'Fair' },
    { value: 'Poor', label: 'Poor' }
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length + images.length > 5) {
      toast.error('Maximum 5 images allowed');
      return;
    }
    setImages(prev => [...prev, ...files]);
  };

  const removeImage = (index) => {
    setImages(prev => prev.filter((_, i) => i !== index));
  };

  const validateForm = () => {
    if (!formData.title.trim()) {
      toast.error('Auction title is required');
      return false;
    }
    if (!formData.description.trim()) {
      toast.error('Auction description is required');
      return false;
    }
    if (!formData.breed.trim()) {
      toast.error('Breed is required');
      return false;
    }
    if (!formData.age || formData.age <= 0) {
      toast.error('Valid age is required');
      return false;
    }
    if (!formData.weight || formData.weight <= 0) {
      toast.error('Valid weight is required');
      return false;
    }
    if (!formData.startingBid || formData.startingBid <= 0) {
      toast.error('Valid starting bid is required');
      return false;
    }
    if (!formData.startDate) {
      toast.error('Start date is required');
      return false;
    }
    if (!formData.endDate) {
      toast.error('End date is required');
      return false;
    }
    if (new Date(formData.startDate) <= new Date()) {
      toast.error('Start date must be in the future');
      return false;
    }
    if (new Date(formData.endDate) <= new Date(formData.startDate)) {
      toast.error('End date must be after start date');
      return false;
    }
    if (images.length === 0) {
      toast.error('At least one image is required');
      return false;
    }
    if (!formData.location.city || !formData.location.state) {
      toast.error('Location is required');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const formDataToSend = new FormData();
      
      // Add form fields
      Object.keys(formData).forEach(key => {
        if (key === 'location') {
          formDataToSend.append(key, JSON.stringify(formData[key]));
        } else {
          formDataToSend.append(key, formData[key]);
        }
      });

      // Add images
      images.forEach((image, index) => {
        formDataToSend.append('images', image);
      });

      const response = await axios.post('/api/auctions', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      toast.success('Auction created successfully!');
      navigate('/auctions', { state: { refresh: true } });
    } catch (error) {
      console.error('Error creating auction:', error);
      const message = error.response?.data?.message || 'Error creating auction';
      toast.error(message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Create New Auction</h1>
          <p className="text-gray-600">List your livestock for auction</p>
        </div>

        <div className="bg-white rounded-lg shadow-sm p-6">
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Basic Information */}
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Auction Details</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="form-label">Auction Title *</label>
                  <input
                    type="text"
                    name="title"
                    className="form-input"
                    placeholder="Enter auction title"
                    value={formData.title}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div>
                  <label className="form-label">Livestock Type *</label>
                  <select
                    name="livestockType"
                    className="form-input"
                    value={formData.livestockType}
                    onChange={handleChange}
                  >
                    {livestockTypes.map(type => (
                      <option key={type.value} value={type.value}>
                        {type.label}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="mt-4">
                <label className="form-label">Description *</label>
                <textarea
                  name="description"
                  rows="4"
                  className="form-input"
                  placeholder="Describe the livestock..."
                  value={formData.description}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            {/* Livestock Details */}
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Livestock Information</h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="form-label">Breed *</label>
                  <input
                    type="text"
                    name="breed"
                    className="form-input"
                    placeholder="Enter breed"
                    value={formData.breed}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div>
                  <label className="form-label">Age (years) *</label>
                  <input
                    type="number"
                    name="age"
                    min="0"
                    className="form-input"
                    placeholder="Enter age"
                    value={formData.age}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div>
                  <label className="form-label">Weight (kg) *</label>
                  <input
                    type="number"
                    name="weight"
                    min="0"
                    step="0.1"
                    className="form-input"
                    placeholder="Enter weight"
                    value={formData.weight}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="mt-4">
                <label className="form-label">Health Status</label>
                <select
                  name="healthStatus"
                  className="form-input"
                  value={formData.healthStatus}
                  onChange={handleChange}
                >
                  {healthStatuses.map(status => (
                    <option key={status.value} value={status.value}>
                      {status.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            {/* Auction Settings */}
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Auction Settings</h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="form-label">Starting Bid (₹) *</label>
                  <input
                    type="number"
                    name="startingBid"
                    min="0"
                    step="0.01"
                    className="form-input"
                    placeholder="Enter starting bid"
                    value={formData.startingBid}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div>
                  <label className="form-label">Min Bid Increment (₹)</label>
                  <input
                    type="number"
                    name="minBidIncrement"
                    min="1"
                    className="form-input"
                    placeholder="Enter minimum increment"
                    value={formData.minBidIncrement}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
                <div>
                  <label className="form-label">Start Date *</label>
                  <input
                    type="datetime-local"
                    name="startDate"
                    className="form-input"
                    value={formData.startDate}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div>
                  <label className="form-label">End Date *</label>
                  <input
                    type="datetime-local"
                    name="endDate"
                    className="form-input"
                    value={formData.endDate}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>

            {/* Location */}
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Location</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="form-label">City *</label>
                  <input
                    type="text"
                    name="location.city"
                    className="form-input"
                    placeholder="Enter city"
                    value={formData.location.city}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div>
                  <label className="form-label">State *</label>
                  <input
                    type="text"
                    name="location.state"
                    className="form-input"
                    placeholder="Enter state"
                    value={formData.location.state}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>

            {/* Images */}
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Livestock Images *</h3>
              <div className="space-y-4">
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
                  <div className="text-center">
                    <Upload className="mx-auto h-12 w-12 text-gray-400" />
                    <div className="mt-4">
                      <label htmlFor="images" className="btn-primary cursor-pointer">
                        <Plus className="w-5 h-5 mr-2" />
                        Upload Images
                      </label>
                      <input
                        id="images"
                        type="file"
                        multiple
                        accept="image/*"
                        className="hidden"
                        onChange={handleImageChange}
                      />
                    </div>
                    <p className="mt-2 text-sm text-gray-600">
                      Upload up to 5 images (JPG, PNG, WebP)
                    </p>
                  </div>
                </div>

                {images.length > 0 && (
                  <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
                    {images.map((image, index) => (
                      <div key={index} className="relative">
                        <img
                          src={URL.createObjectURL(image)}
                          alt={`Preview ${index + 1}`}
                          className="w-full h-24 object-cover rounded-lg"
                        />
                        <button
                          type="button"
                          onClick={() => removeImage(index)}
                          className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600"
                        >
                          <X size={16} />
                        </button>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            <div className="flex justify-end space-x-4">
              <button
                type="button"
                onClick={() => navigate('/auctions')}
                className="btn-secondary"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={loading}
                className="btn-primary flex items-center"
              >
                {loading ? (
                  <>
                    <div className="spinner mr-2"></div>
                    Creating...
                  </>
                ) : (
                  <>
                    <Gavel className="w-5 h-5 mr-2" />
                    Create Auction
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CreateAuction;
