import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { 
  Package, 
  MapPin, 
  Calendar, 
  User, 
  Clock, 
  CheckCircle, 
  XCircle,
  Truck,
  RefreshCw
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

const Bookings = () => {
  const { user } = useAuth();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [role, setRole] = useState('buyer');
  const [statusFilter, setStatusFilter] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [pagination, setPagination] = useState({});

  const statusColors = {
    pending: 'bg-yellow-100 text-yellow-800',
    confirmed: 'bg-blue-100 text-blue-800',
    preparing: 'bg-purple-100 text-purple-800',
    out_for_delivery: 'bg-orange-100 text-orange-800',
    delivered: 'bg-green-100 text-green-800',
    cancelled: 'bg-red-100 text-red-800'
  };

  const statusIcons = {
    pending: Clock,
    confirmed: CheckCircle,
    preparing: Package,
    out_for_delivery: Truck,
    delivered: CheckCircle,
    cancelled: XCircle
  };

  const fetchBookings = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        role,
        page: currentPage,
        limit: 10
      });
      
      if (statusFilter) {
        params.append('status', statusFilter);
      }

      const token = localStorage.getItem('token');
      const response = await axios.get(`/api/bookings?${params.toString()}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setBookings(response.data.bookings);
      setPagination(response.data.pagination);
    } catch (error) {
      console.error('Error fetching bookings:', error);
      toast.error('Error loading bookings');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user) {
      fetchBookings();
    }
  }, [user, role, statusFilter, currentPage]);

  const handleStatusUpdate = async (bookingId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(
        `/api/bookings/${bookingId}/status`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      toast.success('Booking status updated successfully');
      fetchBookings();
    } catch (error) {
      console.error('Error updating status:', error);
      toast.error(error.response?.data?.message || 'Error updating status');
    }
  };

  const handleCancelBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to cancel this booking?')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.patch(
        `/api/bookings/${bookingId}/cancel`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      toast.success('Booking cancelled successfully');
      fetchBookings();
    } catch (error) {
      console.error('Error cancelling booking:', error);
      toast.error(error.response?.data?.message || 'Error cancelling booking');
    }
  };

  const formatCurrency = (amount) => {
    return `â‚¹${parseFloat(amount).toLocaleString('en-IN')}`;
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-IN');
  };

  const getStatusActions = (booking) => {
    if (role === 'seller') {
      switch (booking.status) {
        case 'pending':
          return (
            <div className="flex space-x-2">
              <button
                onClick={() => handleStatusUpdate(booking._id, 'confirmed')}
                className="btn-primary text-sm px-3 py-1"
              >
                Confirm
              </button>
              <button
                onClick={() => handleStatusUpdate(booking._id, 'cancelled')}
                className="btn-secondary text-sm px-3 py-1"
              >
                Reject
              </button>
            </div>
          );
        case 'confirmed':
          return (
            <button
              onClick={() => handleStatusUpdate(booking._id, 'preparing')}
              className="btn-primary text-sm px-3 py-1"
            >
              Start Preparing
            </button>
          );
        case 'preparing':
          return (
            <button
              onClick={() => handleStatusUpdate(booking._id, 'out_for_delivery')}
              className="btn-primary text-sm px-3 py-1"
            >
              Out for Delivery
            </button>
          );
        case 'out_for_delivery':
          return (
            <button
              onClick={() => handleStatusUpdate(booking._id, 'delivered')}
              className="btn-primary text-sm px-3 py-1"
            >
              Mark Delivered
            </button>
          );
        default:
          return null;
      }
    } else {
      // Buyer actions
      if (['pending', 'confirmed', 'preparing'].includes(booking.status)) {
        return (
          <button
            onClick={() => handleCancelBooking(booking._id)}
            className="btn-secondary text-sm px-3 py-1"
          >
            Cancel
          </button>
        );
      }
      return null;
    }
  };

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Please Login</h2>
          <p className="text-gray-600">You need to be logged in to view bookings.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">My Bookings</h1>
              <p className="text-gray-600">Manage your product bookings and deliveries</p>
            </div>
            <button
              onClick={fetchBookings}
              className="btn-secondary flex items-center space-x-2"
              disabled={loading}
            >
              <RefreshCw size={16} className={loading ? 'animate-spin' : ''} />
              <span>Refresh</span>
            </button>
          </div>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
          <div className="flex flex-wrap gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">View as</label>
              <select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className="form-input"
              >
                <option value="buyer">Buyer</option>
                <option value="seller">Seller</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Status</label>
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="form-input"
              >
                <option value="">All Status</option>
                <option value="pending">Pending</option>
                <option value="confirmed">Confirmed</option>
                <option value="preparing">Preparing</option>
                <option value="out_for_delivery">Out for Delivery</option>
                <option value="delivered">Delivered</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>
          </div>
        </div>

        {/* Bookings List */}
        {loading ? (
          <div className="flex justify-center py-12">
            <div className="spinner"></div>
          </div>
        ) : (
          <div>
            {bookings.length === 0 ? (
              <div className="text-center py-12">
                <div className="text-gray-400 mb-4">
                  <Package size={48} className="mx-auto" />
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">No bookings found</h3>
                <p className="text-gray-600">
                  {role === 'buyer' 
                    ? "You haven't made any bookings yet." 
                    : "You don't have any incoming bookings yet."
                  }
                </p>
              </div>
            ) : (
              <div className="space-y-6">
                {bookings.map((booking) => {
                  const StatusIcon = statusIcons[booking.status];
                  return (
                    <div key={booking._id} className="bg-white rounded-lg shadow-sm border p-6">
                      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
                        <div className="flex-1">
                          <div className="flex items-start space-x-4">
                            <div className="w-16 h-16 bg-gray-200 rounded-lg flex items-center justify-center">
                              {booking.product.images && booking.product.images[0] ? (
                                <img
                                  src={booking.product.images[0]}
                                  alt={booking.product.name}
                                  className="w-full h-full object-cover rounded-lg"
                                />
                              ) : (
                                <Package size={24} className="text-gray-400" />
                              )}
                            </div>
                            
                            <div className="flex-1">
                              <div className="flex items-center space-x-2 mb-2">
                                <h3 className="text-lg font-semibold text-gray-900">
                                  {booking.product.name}
                                </h3>
                                <span className={`px-2 py-1 rounded-full text-xs font-medium ${statusColors[booking.status]}`}>
                                  <div className="flex items-center space-x-1">
                                    <StatusIcon size={12} />
                                    <span>{booking.status.replace('_', ' ').toUpperCase()}</span>
                                  </div>
                                </span>
                              </div>
                              
                              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 text-sm text-gray-600">
                                <div className="flex items-center space-x-1">
                                  <User size={14} />
                                  <span>
                                    {role === 'buyer' 
                                      ? `Seller: ${booking.seller.name}`
                                      : `Buyer: ${booking.buyer.name}`
                                    }
                                  </span>
                                </div>
                                
                                <div className="flex items-center space-x-1">
                                  <Package size={14} />
                                  <span>{booking.quantity} {booking.product.unit}</span>
                                </div>
                                
                                <div className="flex items-center space-x-1">
                                  <span className="font-medium">{formatCurrency(booking.totalAmount)}</span>
                                  {booking.deliveryFee > 0 && (
                                    <span className="text-xs text-gray-500">(+â‚¹{booking.deliveryFee} delivery)</span>
                                  )}
                                </div>
                                
                                <div className="flex items-center space-x-1">
                                  <Calendar size={14} />
                                  <span>{formatDate(booking.createdAt)}</span>
                                </div>
                              </div>

                              {booking.deliveryAddress && (
                                <div className="mt-3 flex items-start space-x-1 text-sm text-gray-600">
                                  <MapPin size={14} className="mt-0.5" />
                                  <span>
                                    {booking.deliveryAddress.street}, {booking.deliveryAddress.city}, {booking.deliveryAddress.district}
                                  </span>
                                </div>
                              )}

                              {booking.estimatedDeliveryDate && (
                                <div className="mt-2 text-sm text-gray-600">
                                  <span className="font-medium">Estimated Delivery:</span> {formatDate(booking.estimatedDeliveryDate)}
                                </div>
                              )}
                            </div>
                          </div>
                        </div>
                        
                        <div className="mt-4 lg:mt-0 lg:ml-4">
                          {getStatusActions(booking)}
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}

            {/* Pagination */}
            {pagination.total > 1 && (
              <div className="mt-8 flex justify-center">
                <div className="flex space-x-2">
                  <button
                    onClick={() => setCurrentPage(currentPage - 1)}
                    disabled={!pagination.hasPrev}
                    className="btn-secondary px-3 py-2 disabled:opacity-50"
                  >
                    Previous
                  </button>
                  
                  <span className="flex items-center px-3 py-2 text-gray-700">
                    Page {pagination.current} of {pagination.total}
                  </span>
                  
                  <button
                    onClick={() => setCurrentPage(currentPage + 1)}
                    disabled={!pagination.hasNext}
                    className="btn-secondary px-3 py-2 disabled:opacity-50"
                  >
                    Next
                  </button>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Bookings;
